var _envet = (function () {
    envetObj = {}
    return {
        //   添加事件
        on: function (type, envet) {
            //   添加事件
            (envetObj[type] || (envetObj[type] = [])).push(envet)
        },
        //   触发事件
        emit: function (type) {
            let args = Array.prototype.slice.call(arguments, 1)
            let _envets = envetObj[type]
            if (!_envets) return
            for (let j = 0; j < _envets.length; j++) {
                _envets[j].apply(null,args)
            }
        },
        //   移除事件
        off: function (type, envet) {
            if (arguments.length == 0) {
                envetObj = {}
            } else if (arguments.length == 1) {
                envetObj[type] = []
            } else if (arguments.length == 2) {
                let removeEnvetObj = envetObj[type]
                if (!removeEnvetObj) return
                for (let i = removeEnvetObj.length - 1; i >= 0; i--) {
                    if (removeEnvetObj[i] === envet) removeEnvetObj.splice(i, 1)
                }
            }
        }
    }
}())